mapper.select:
SELECT , (SELECT COUNT(*) FROM community_comment_tbl c WHERE c.community_id = community_tbl.community_id AND c.deleted_at IS NULL ) AS comment_count FROM community_tbl WHERE status = 'ACTIVE'
AND theme_id = #{themeId}
ORDER BY created_at DESC, community_id DESC LIMIT #{offset}, #{limit}

SELECT , (SELECT COUNT(*) FROM community_comment_tbl c WHERE c.community_id = community_tbl.community_id AND c.deleted_at IS NULL ) AS comment_count FROM community_tbl WHERE status = 'ACTIVE'
AND theme_id = #{themeId}
AND ( title LIKE CONCAT('%', #{q}, '%') OR content LIKE CONCAT('%', #{q}, '%') )
ORDER BY created_at DESC, community_id DESC LIMIT #{offset}, #{limit}

mapper.update:
UPDATE community_tbl
SET title = #{title},
    content = #{content}
WHERE community_id = #{communityId}
  AND status = 'ACTIVE'
